mapper.select:
       select  DM.message_id  ARTICLEID,DM.message_title TITLE,DM.message_content CONTENT,
       date_format(DM.create_time,'%Y-%m-%d %H:%i:%s') ADDTIME,DM.user_name USERNAME from doc_message DM
        WHERE 1=1
       
           AND (DM.message_title LIKE  CONCAT('%',#{name},'%')or
           DM.message_content LIKE  CONCAT('%',#{name},'%')
           )
       
       
           AND year(DM.create_time)=#{year}
       
       
           AND month(DM.create_time)=#{month}
       
order  by DM.create_time desc
      limit #{pageNum},#{pageSize}

        select count(*) from doc_message DM
        WHERE 1=1
        
            AND (DM.message_title LIKE  CONCAT('%',#{name},'%')or
            DM.message_content LIKE  CONCAT('%',#{name},'%')
            )
        
        
            AND year(DM.create_time)=#{year}
        
        
            AND month(DM.create_time)=#{month}
