mapper.select:
SELECT platform.system_id AS systemId, platform.system_name AS systemName, platform.system_key AS systemKey, platform.valid_flag AS validFlag, platform.create_time AS createTime, platform.update_time AS updateTime, platform.create_user_id AS createUserId, users.user_name AS createUserName FROM platform_system_info platform LEFT JOIN sys_users users ON platform.create_user_id = users.user_id
and platform.system_name LIKE CONCAT('%',#{systemName},'%')
LIMIT #{startIndex}, #{pageSize}
